import React from 'react';
import { ReactFlow, ReactFlowProvider, Background, Handle, MarkerType, MiniMap, Position } from '@xyflow/react';

import '@xyflow/react/dist/style.css';

const initialNodes = [
  { id: '1', sourcePosition: 'right', type: 'input', position: { x: 20, y: 50 }, data: { label: 'Skills' } },
  { id: '2', sourcePosition: 'right', targetPosition: 'left', position: { x: 250, y: 150 }, data: { label: '2' } },

];

{/*use edges to connect nodes by source & target;
for ex., source 1 connects node 1 to target node 2*/}
const initialEdges = [{ id: '1->2', source: '1', target: '2', animated: true },
                      { id: '2->3', source: '2', target: '3' },
                      { id: '3->4', source: '3', target: '4' }
];

export default function App() {
  return (
    <ReactFlowProvider>
    <div style={{ width: '100vw', height: '100vh' }}>
      <ReactFlow nodes={initialNodes} edges={initialEdges} />
      <Background />
    </div>
    </ReactFlowProvider>
  );
}
