import React from 'react';
import { ReactFlow, ReactFlowProvider, Background, MiniMap } from '@xyflow/react';

import '@xyflow/react/dist/style.css';

const initialNodes = [
  { id: '1', position: { x: 0, y: 0 }, data: { label: '1' } },
  { id: '2', position: { x: 0, y: 100 }, data: { label: '2' } },
  { id: '3', position: { x: 0, y: 150 }, data: { label: '3'} },
  { id: '4', position: { x: 0, y: 250 }, data: { label: '4'} },
];

{/*use edges to connect nodes by source & target;
for ex., source 1 connects node 1 to target node 2*/}
const initialEdges = [{ id: '1->2', source: '1', target: '2', animation: 'true ' },
                      { id: '2->3', source: '2', target: '3' },
                      { id: '3->4', source: '3', target: '4' }
];

export default function App() {
  return (
    <ReactFlowProvider>
    <div style={{ width: '100vw', height: '100vh' }}>
      <ReactFlow nodes={initialNodes} edges={initialEdges} />
      <Background />
    </div>
    </ReactFlowProvider>
  );
}
